// src/App.js
import React, { useEffect, useState } from 'react';
import KanbanBoard from './components/KanbanBoard';
import GroupControls from './components/GroupControls';
import { fetchTickets } from './utils/api';

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState(localStorage.getItem('groupBy') || 'status');
  const [sortBy, setSortBy] = useState('priority');

  useEffect(() => {
    async function loadTickets() {
      try {
        const data = await fetchTickets();
        setTickets(data);
      } catch (error) {
        console.error('Failed to fetch tickets:', error);
      }
    }
    loadTickets();
  }, []);

  useEffect(() => {
    localStorage.setItem('groupBy', groupBy);
  }, [groupBy]);

  const handleGroupChange = (newGroup) => setGroupBy(newGroup);
  const handleSortChange = (newSort) => setSortBy(newSort);

  return (
    <div className="app">
      <GroupControls onGroupChange={handleGroupChange} onSortChange={handleSortChange} />
      <KanbanBoard tickets={tickets} groupBy={groupBy} sortBy={sortBy} />
    </div>
  );
}

export default App;
