import React, { useState, useEffect } from 'react';
import { fetchTickets } from '../services/api';
import Card from './Card';

const KanbanBoard = () => {
    const [tickets, setTickets] = useState([]);  // State for storing tickets
    const [error, setError] = useState(null);    // State for storing errors, if any

    useEffect(() => {
        // Fetch tickets from the API when the component mounts
        const getTickets = async () => {
            try {
                const data = await fetchTickets();
                setTickets(data);  // Set tickets state with the fetched data
            } catch (err) {
                setError("Failed to fetch tickets.");
            }
        };
        getTickets();
    }, []);

    return (
        <div className="kanban-board">
            <h1>Kanban Board</h1>
            {error && <p>{error}</p>}
            <div className="columns">
                {/* Display tickets in a basic list for now, grouped by status */}
                {['Todo', 'In Progress', 'Done', 'Canceled'].map((status) => (
                    <div key={status} className="column">
                        <h2>{status}</h2>
                        {tickets
                            .filter(ticket => ticket.status === status)
                            .map(ticket => (
                                <Card
                                    key={ticket.id}
                                    title={ticket.title}
                                    priority={ticket.priority}
                                />
                            ))
                        }
                    </div>
                ))}
            </div>
        </div>
    );
};

export default KanbanBoard;
