import React from 'react';
import TicketCard from './TicketCard';

const KanbanBoard = ({ tickets }) => {
  // Define all columns you want to display
  const columns = ['Todo', 'In progress', 'Done', 'Backlog','Cancel'];

  return (
    <div className="kanban-board">
      {columns.map((column) => (
        <div key={column} className="kanban-column">
          <h2>{column}</h2>
          {tickets
            .filter((ticket) => ticket.status === column)
            .map((ticket) => (
              <TicketCard key={ticket.id} ticket={ticket} />
            ))}
          {/* Show a message if the column is empty */}
          {tickets.filter((ticket) => ticket.status === column).length === 0 && (
            <p className="empty-column">No tasks in this status</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default KanbanBoard;
