import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status'); // Default grouping by status
  const [sortOption, setSortOption] = useState('priority'); // Default sort by priority
  const [groupedTickets, setGroupedTickets] = useState({});

  // Fetch data from the API
  useEffect(() => {
    fetch('https://api.quicksell.co/v1/internal/frontend-assignment')
      .then(response => response.json())
      .then(data => {
        setTickets(data.tickets);
        groupAndSortTickets(data.tickets, groupBy, sortOption);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Handle grouping and sorting when options change
  useEffect(() => {
    groupAndSortTickets(tickets, groupBy, sortOption);
  }, [groupBy, sortOption, tickets]);

  // Function to group and sort tickets
  const groupAndSortTickets = (tickets, groupBy, sortOption) => {
    const grouped = tickets.reduce((acc, ticket) => {
      const key = ticket[groupBy];
      if (!acc[key]) acc[key] = [];
      acc[key].push(ticket);
      return acc;
    }, {});

    // Sort tickets in each group
    Object.keys(grouped).forEach(group => {
      grouped[group].sort((a, b) => {
        if (sortOption === 'priority') {
          return b.priority - a.priority; // Descending order for priority
        } else if (sortOption === 'title') {
          return a.title.localeCompare(b.title); // Ascending order for title
        }
        return 0;
      });
    });

    setGroupedTickets(grouped);
  };

  // Render controls for grouping and sorting
  return (
    <div className="app">
      <div className="controls">
        <button onClick={() => setGroupBy('status')}>Group by Status</button>
        <button onClick={() => setGroupBy('user')}>Group by User</button>
        <button onClick={() => setGroupBy('priority')}>Group by Priority</button>
        <button onClick={() => setSortOption('priority')}>Sort by Priority</button>
        <button onClick={() => setSortOption('title')}>Sort by Title</button>
      </div>

      <div className="kanban-board">
        {Object.keys(groupedTickets).map(group => (
          <div className="kanban-column" key={group}>
            <h2>{groupBy.charAt(0).toUpperCase() + groupBy.slice(1)}: {group}</h2>
            {groupedTickets[group].map(ticket => (
              <div className="ticket-card" key={ticket.id}>
                <h3>{ticket.title}</h3>
                <p><strong>Status:</strong> {ticket.status}</p>
                <p><strong>User:</strong> {ticket.user}</p>
                <p><strong>Priority:</strong> {ticket.priority}</p>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
